/*
@author: Eligijus Skersonas
*/

import React, { Component } from "react";

class Features extends Component {
  state = {
    yearBuilt: "1969",
    taxAmount: "4542.86",
    noOfBathrooms: "2",
    noOfBedrooms: "3",
    area: "1476",
    errors: {
      yearBuilt: [],
      taxAmount: [],
      noOfBathrooms: [],
      noOfBedrooms: [],
      area: []    
    },
    errorQuantity: 0
  };

  centreContent = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  };

  render() {
    return this.renderForm();
  }

  handleSubmit = (e) => {
    this.validateNumberOnlyString("noOfBedrooms");
    this.validateNumberOnlyString("yearBuilt");
    this.validateNumberOnlyString("taxAmount");
    this.validateNumberOnlyString("noOfBathrooms");
    this.validateNumberOnlyString("area");
    if (!this.validateNumberOnlyString(this.state.noOfBedrooms)){
      this.state.errors.noOfBedrooms.push("Must enter number.");
    }
    if (!this.validateNumberOnlyString(this.state.noOfBathrooms)){
      this.state.errors.noOfBathrooms.push("Must enter number.");
    }
    if (!this.validateNumberOnlyString(this.state.taxAmount)){
      this.state.errors.taxAmount.push("Must enter number.");
    }
    if (!this.validateNumberOnlyString(this.state.area)){
      this.state.errors.area.push("Must enter number.");
    }
    if (!this.validateNumberOnlyString(this.state.yearBuilt)){
      this.state.errors.yearBuilt.push("Must enter number.");
    }
  }

  validateNumberOnlyString = (property) => {
    var rgx = /^[0-9]*\.?[0-9]*$/;
    if (!this.state[property].match(rgx)){
      this.state.errors[property].push("Must enter number.");
    }
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  };

  renderForm() {
    const inputClass = "form-control rounded-pill";
    const submitClass = "form-control rounded-pill bg-dark";
    const submitStyle = { color: "white" };

    return (
      <div className="row m-4">
        <div className="col">
          <input
            name="noOfBedrooms"
            type="text"
            className={inputClass}
            placeholder="Number Of Bedrooms"
            aria-label="Number Of Bedrooms"
            onChange={this.handleChange}
            value={this.state.noOfBedrooms}
          />
        </div>
        <div className="col">
          <input
            name="noOfBathrooms"
            type="text"
            className={inputClass}
            placeholder="Number Of Bathrooms"
            aria-label="Number Of Bathrooms"
            onChange={this.handleChange}
            value={this.state.noOfBathrooms}
          />
        </div>
        <div className="col">
          <input
            name="taxAmount"
            type="text"
            className={inputClass}
            placeholder="Tax Amount"
            aria-label="Tax Amount"
            onChange={this.handleChange}
            value={this.state.taxAmount}
          />
        </div>
        <div className="col">
          <input
            name="yearBuilt"
            type="text"
            className={inputClass}
            placeholder="Year Built"
            aria-label="Year Built"
            onChange={this.handleChange}
            value={this.state.yearBuilt}
          />
        </div>
        <div className="col">
          <input
            name="area"
            type="text"
            className={inputClass}
            placeholder="Area"
            aria-label="Area"
            onChange={this.handleChange}
            value={this.state.area}
          />
        </div>
        <div className="col">
          <button
            type="submit"
            style={submitStyle}
            className={submitClass}
            aria-label="Predict"
            onClick={() => this.handleSubmit}
          >
            Predict
          </button>
        </div>
      </div>
    );
  }
}

export default Features;
