import "chart.js/auto";
import { Bar } from "react-chartjs-2";
import React, { Component } from "react";

class BarChart extends Component {
  constructor(props) {
    super(props);

    this.state = {
      taxAmount: props.state.taxAmount,
      noOfBedrooms: props.state.noOfBedrooms,
      noOfBathrooms: props.state.noOfBathrooms,
      area: props.state.area,
      yearBuilt: props.state.area,
    };

    this.chartState = {
      data: {
        labels: [
          "NumberOfBedrooms",
          "NumberOfBathrooms",
          "TaxAmount",
          "Area",
          "YearBuilt",
        ],
        datasets: [
          {
            label: "User Input",
            data: [
              this.state.noOfBedrooms,
              this.state.noOfBathrooms,
              this.state.taxAmount,
              this.state.area,
              this.state.yearBuilt,
            ],
            backgroundColor: [
              "rgba(255, 99, 132, 0.6)",
              "rgba(54, 162, 235, 0.6)",
              "rgba(255, 206, 86, 0.6)",
              "rgba(75, 192, 192, 0.6)",
              "rgba(153, 102, 255, 0.6)",
            ],
          },
        ],
      },
    };
  }

  render() {
    this.state = this.props.state;
    return (
      <div style={{ height: "80vh" }}>
        {this.setState(this.props.state)}
        <Bar
          data={this.chartState.data}
          options={{ responsive: true, maintainAspectRatio: false }}
        />
      </div>
    );
  }
}

export default BarChart;
